sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var fecha_value = g_form.getValue('u_slx_fecha');

    var ga = new GlideAjax("slx_soporte_sn");
    ga.addParam('sysparm_name', 'validar_fechas');
    ga.addParam('sysparm_date', fecha_value);
    ga.getXML(handleResponse);

}

function handleResponse(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.addInfoMessage(answer);
    if (answer == "false") {
		g_form.addInfoMessage(answer);
        g_form.clearValue('u_slx_fecha');
        g_form.showFieldMsg('u_slx_fecha', 'La fecha no puede ser futura', 'error');
    }
}
