sys_script_include.script:
var slx_soporte_sn = Class.create();
slx_soporte_sn.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	validar_fechas: function(){

        // Se crean GlideDateTime para la fecha actual y para el campo ingresado
        var rightnow = new GlideDateTime();
        var fieldDate = new GlideDateTime();
        fieldDate.setDisplayValue(this.getParameter('sysparm_date'));
        // Se obtiene la porcion de fecha (sin hora) de las variables
        var today = rightnow.getLocalDate();
        var istoday = fieldDate.getLocalDate();

        //valida que sea una fecha mayor a la actual
        if (istoday.compareTo(today) != 1) {
            return true;

        } else {
            return false;
        }
	},

    type: 'slx_soporte_sn'
});
